import React ,{useState, useEffect} from 'react';
import {StyleSheet,Text, SafeAreaView,View, Button, Image,KeyboardAvoidingView,TextInput,TouchableOpacity,Alert} from 'react-native';

export default function Add_task(){
  return(
      <SafeAreaView style={StyleSheet.container}>
        <KeyboardAvoidingView 
          behavior={Platform.OS === 'ios' ? "padding":"height"}
          style={styles.AddTaskWrapper} 
        >
          
          <TextInput style={styles.inputsection} placeholder={'Title'}></TextInput>


          {/* <TextInput style={styles.notessection} placeholder={'Title'}></TextInput> */}


          <TouchableOpacity onPress={()=>{Alert.alert('Button with adjusted color pressed')}}>
            <View style={styles.continuebutton}>
              <Text style={styles.addText}>{"Continue >>>"}</Text>
            </View>
          </TouchableOpacity>


        </KeyboardAvoidingView>
      </SafeAreaView>



  );
}

const styles=StyleSheet.create({
  container:{

  },

  AddTaskWrapper:{

  },

  inputsection:{
    marginTop: 20,
    paddingVertical: 10,
    backgroundColor: '#f3f7ff',
    textAlign: 'left',
    fontSize: 20,
  },

  // the continue button
  continuebutton:{
    marginTop: 20,
    paddingVertical: 20,
    backgroundColor: "#187C04",
    textAlign: 'left',
    fontSize: 20,
  }



})









