import React, {useState, useEffect} from 'react'
import {View, Text, TextInput, StyleSheet} from 'react-native'
import firebase from 'firebase/compat/app'
import {db} from 'firebase'
import {connect} from 'react-redux'
import { addDoc, collection, serverTimestamp, query,
  orderBy,
  onSnapshot,
  limit, } from "firebase/firestore";
//I thought that we'd use the two below, theyre just for push notifications apparently
//import messaging from '@react-native-firebase/messaging';

export default function Chat(){

  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const scroll = useRef();

  const onEnter = async (event) => {
    event.preventDefault();
    if (message.trim() === "") {
      alert("Enter valid message");
      return;
    }
    const { uid, displayName, photoURL } = firebase.auth().currentUser;

    console.log(firebase.auth().currentUser)

    await addDoc(collection(db, "messages"), {
      text: message,
      name: displayName,
      avatar: photoURL,
      createdAt: serverTimestamp(),
    });
    setMessage("")
    scroll.current.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    const q = query(
      collection(db, "messages"),
      orderBy("createdAt"),
      limit(50)
    );

    const unsubscribe = onSnapshot(q, (QuerySnapshot) => {
      let messages = [];
      QuerySnapshot.forEach((doc) => {
        messages.push({ ...doc.data(), id: doc.id });
      });
      setMessages(messages);
    });
    return () => unsubscribe;
  }, []);

  return (
    <View>
        <Text style = {styles.text}>{messages?.map((message) => (
          <Text key={message.id}>{message}</Text>
        ))}</Text>

        <TextInput 
            scroll = {scroll}
            style = {styles.textentry}
            placeholder='' 
            onChangeText={message => setMessage(message)}
            onSubmitEditing = {(value) => onEnter(value)}
        />

    </View>
  )
}

const styles = StyleSheet.create({
  text:{
    margin: 20,
    fontSize: 30,
  },
  textentry:{
    height: 50,
    backgroundColor: 'gray',
    alignItems: 'center',
    marginHorizontal: 40,
    borderRadius: 20,
    marginBottom:30,
    padding: 15,

  }

});
