import React ,{useState, useEffect} from 'react';
import {StyleSheet,Text, SafeAreaView,View, Button, Image,KeyboardAvoidingView,TextInput,TouchableOpacity,Alert} from 'react-native';

//added
import {Camera} from 'expo-camera';
import * as ImagePicker from 'expo-image-picker'

export default function Add_task(){
  //added
  const [hasCameraPermission, setHasCameraPermission] = useState(null);
  const [type, setType] = useState(Camera.Constants.Type.Back);
  const [camera, setCamera] = useState(null);
  const [image, setImage] = useState(null);
  const [hasGalleryPermission, setHasGalleryPermission] = useState(null);




  //dummy 
  const TaskInit={
    title:null
  }

  //task state
  const [Task,SetTask] = useState(TaskInit);

  

  //handlers
  const handletitlechange= (t)=>{
    SetTask(
      {
        ...Task,
        title:t,
      }
    )
  }

  const handleaddtask = ()=>{
    
    
  }

  //added
  useEffect(() => {
    (async ()=> {
      const cameraStatus = await Camera.requestCameraPermissionsAsync();
      setHasCameraPermission(cameraStatus === 'granted');
      
      const galleryStatus = await ImagePicker.requestMediaLibraryPermissionsAsync();
      setHasGalleryPermission(galleryStatus === 'granted');

    })();
  }, []);

  if (hasCameraPermission === null || hasGalleryPermission === null){
    console.log("asdfkjasldfjlkasdjflksdajfkjsadlkfjsadfjlsda\n\n\n\n\n");
    return <View/>;

  }
  if(hasCameraPermission === false){
    //return <Text>No access</Text>
  }

  const takePicture = async () => {
    if(camera){
      const data = await camera.takePictureAsync(null);
      setImage(data.uri); 
      console.log(data.uri)
    }
  }

  const pickImage = async () => {
    let result = await ImagePicker.launchImageLibraryAsync({
      mediaTypes: ImagePicker.MediaTypeOptions.Images,
      allowsEditing: true,
      aspect: [1,1],
      quality: 1,
    });

    console.log(result+"\n");

    if (!result.canceled){
      setImage(result.uri);
    }
  };


  return(
      
      
      
      


      <SafeAreaView style={StyleSheet.container}>
        {/* added */}
        
        <View>
          <Text>mdfk</Text>
       <View >
        <Camera 
          ref = {ref => setCamera(ref)}
          style = {styles.fixedRatio}
          type = {type}
          ratio = {'1:1'}
        />
      </View>
      <Button
        title = "Flip Image"
        onPress = {() => {
          setType(
            type === Camera.Constants.Type.back ? Camera.Constants.Type.front : Camera.Constants.Type.back
          );
        }}>

      </Button>
      <Button title = "Take Picture" onPress={()=> takePicture()}/>
      <Button title = "Pick Image" onPress={()=> pickImage()}/>
      <Button title = "Save" onPress = {() => navigation.navigate('Save',{image})}/>
      {image && <Image source = {{uri:image}} style = {{flex: 1}}/>}
    </View>

    






        <KeyboardAvoidingView 
          behavior={Platform.OS === 'ios' ? "padding":"height"}
          style={styles.AddTaskWrapper} 
        >

          <Text style={styles.title}>Task Title</Text>
          <TextInput style={styles.titleinput} placeholder={'Title'} value={Task.title} onChangeText={text => handletitlechange(text)}></TextInput>


          {/* <TextInput style={styles.notessection} placeholder={'Title'}></TextInput> */}


          <TouchableOpacity onPress={()=>handleaddtask()}>
            <View style={styles.continuebutton}>
              <Text style={styles.continuetext}>{"Continue >>>"}</Text>
            </View>
          </TouchableOpacity>


        </KeyboardAvoidingView>
      </SafeAreaView>



  );
}

const styles=StyleSheet.create({


  //mine
  container:{

  },

  AddTaskWrapper:{

  },

  title:{

  },

  titleinput:{
    marginTop: 20,
    paddingVertical: 10,
    backgroundColor: '#f3f7ff',
    textAlign: 'left',
    fontSize: 20,
  },

  // the continue button
  continuebutton:{
    marginTop: 20,
    paddingVertical: 20,
    backgroundColor: "#187C04",
  },

  continuetext:{
    color:"white",
    textAlign: 'center',
    fontSize: 20,
  }



})









