import React ,{useState, useEffect} from 'react';
import {StyleSheet,Text, SafeAreaView,View, Button, Image,KeyboardAvoidingView,TextInput,TouchableOpacity,Alert} from 'react-native';

//added
import {Camera} from 'expo-camera';
import * as ImagePicker from 'expo-image-picker';

export default function Add_task({navigation}){
  //dummy 
  const TaskInit={
    title:null
  }

  //task state
  const [Task,SetTask] = useState(TaskInit);

  

  //handlers
  const handletitlechange= (t)=>{
    SetTask(
      {
        ...Task,
        title:t,
      }
    )
  }

  const handleaddtask = ()=>{
    
    
  }




  return(
      <SafeAreaView style={StyleSheet.container}>
        <View>
        <Text style={styles.title}>Task Title</Text>
          <TextInput style={styles.titleinput} placeholder={'Title'} value={Task.title} onChangeText={text => handletitlechange(text)}></TextInput>

          <Text style={styles.title}>Notes</Text>
          <TextInput style={styles.titleinput} placeholder={'Add some notes here...'} value={Task.title} onChangeText={text => handletitlechange(text)}></TextInput>

          <TextInput
            style={styles.titleinput}
            // value={v0KC=}

            placeholder={'Add some notes here...'}

            // onChangeText={onChangeText}
            multiline={true}
            numberOfLines={4}
            // multiline={multiline || false}
            // ref={(r) => { inputRef && inputRef(r); }}
    />
        </View>
        <KeyboardAvoidingView 
          behavior={Platform.OS === 'ios' ? "padding":"height"}
          style={styles.AddTaskWrapper} 
        >

          {/* <Text style={styles.title}>Task Title</Text>
          <TextInput style={styles.titleinput} placeholder={'Title'} value={Task.title} onChangeText={text => handletitlechange(text)}></TextInput>

          <Text style={styles.title}>Notes</Text>
          <TextInput style={styles.titleinput} placeholder={'Add some notes here...'} value={Task.title} onChangeText={text => handletitlechange(text)}></TextInput>

          <TextInput
            style={styles.titleinput}
            // value={v0KC=}

            placeholder={'Add some notes here...'}

            // onChangeText={onChangeText}
            multiline={true}
            numberOfLines={4}
            // multiline={multiline || false}
            // ref={(r) => { inputRef && inputRef(r); }}
    /> */}

          <TouchableOpacity onPress={()=>handleaddtask()}>
            <View style={styles.continuebutton}>
              <Text style={styles.continuetext}>{"Continue >>>"}</Text>
            </View>
          </TouchableOpacity>


        </KeyboardAvoidingView>
      </SafeAreaView>
  );
}

const styles=StyleSheet.create({



  //mine
  container:{

  },

  AddTaskWrapper:{
    marginTop:250,

  },

  title:{

  },

  titleinput:{
    marginTop: 20,
    paddingVertical: 10,
    backgroundColor: '#f3f7ff',
    textAlign: 'left',
    fontSize: 20,
  },

  // the continue button
  continuebutton:{
    marginTop: 20,
    paddingVertical: 20,
    backgroundColor: "#187C04",
  },

  continuetext:{
    color:"white",
    textAlign: 'center',
    fontSize: 20,
  }



})









